import React, { useState, useEffect } from "react"
import axios from "axios"

const Filter = ({ filter, handlefilterChange }) => {
	return (
		<div>
			<b>
				Find Countries <input value={filter} onChange={handlefilterChange} />
			</b>
		</div>
	)
}

const Countries = ({ filteredCountries }) => {
	const ShowOne = (
		<div>
			<h1>{filteredCountries.name}</h1>
			<p>capital: {filteredCountries.capital}</p>
			<p>population: {filteredCountries.population}</p>
			<h2>languages</h2>
			<ul>
				{(filteredCountries.languages || []).map((language) => (
					<li key={language.name}>{language.name}</li>
				))}
			</ul>
			<img src={filteredCountries.flag} alt={filteredCountries.name} />
		</div>
	)
	const ShowTen = (
		<div>
			{filteredCountries.map((country) => (
				<div key={country.name.common}>{country.name.common}</div>
			))}
		</div>
	)
	const DontShow = <div> Too many matches, specify another filter </div>
	if (filteredCountries.length > 10) return DontShow
	else if (filteredCountries.length === 1) return ShowOne
	else return ShowTen
}

const App = () => {
	const [country, setCountry] = useState([])
	const [filter, setFilter] = useState("")

	const hook = () => {
		console.log("effect")
		axios.get("https://restcountries.com/v3.1/all").then((response) => {
			console.log("promise fulfilled")
			setCountry(response.data)
		})
	}
	useEffect(hook, [])

	const filteredCountries = country.filter((country) =>
		country.name.common.toLowerCase().includes(filter.toLowerCase())
	)
	const handlefilterChange = (event) => {
		setFilter(event.target.value)
	}

	return (
		<div>
			<h2>Countries.</h2>
			<Filter filter={filter} handlefilterChange={handlefilterChange} />
			<Countries filteredCountries={filteredCountries} />
		</div>
	)
}

export default App
