import React, { useState, useEffect } from "react"
import axios from "axios"

const button = ({ country }) => {
	var show = false
	return (
		<div key={country.name.common}>
			{country.name.common}
			<button
				onClick={() => {
					show = true
				}}
			>
				Show
			</button>
			<div>{show && Show(country)}</div>
		</div>
	)
}

const Filter = ({ filter, handlefilterChange }) => {
	return (
		<div>
			<b>
				Find Countries <input value={filter} onChange={handlefilterChange} />
			</b>
		</div>
	)
}
const Show = (Country) => {
	return (
		<div>
			<h1>{Country.name.common}</h1>
			<div>
				<b>Capital:</b> {Country.capital}
			</div>
			<div>
				<b>Population:</b> {Country.population}
				{console.log("Carlos es gay ", Country.name.common)}
			</div>
			<h2>Languages</h2>
			<ul>
				{Object.keys(Country.languages || []).map((language) => (
					<li key={language}>{Country.languages[language]}</li>
				))}
			</ul>
			<img src={Country.flags.svg} alt={Country.name} width='200' />
		</div>
	)
}

const Countries = ({ filteredCountries }) => {
	const ShowTen = filteredCountries.map((country) => (
		<>
			<>{(show = false)}</>
		</>
	))

	const DontShow = <div> Too many matches, specify another filter </div>
	if (filteredCountries.length > 10) return DontShow
	else if (filteredCountries.length === 1) return Show(filteredCountries[0])
	else return ShowTen
}

const App = () => {
	const [country, setCountry] = useState([])
	const [filter, setFilter] = useState("")
	const hook = () => {
		console.log("effect")
		axios.get("https://restcountries.com/v3.1/all").then((response) => {
			console.log("promise fulfilled")
			setCountry(response.data)
		})
	}
	useEffect(hook, [])

	const filteredCountries = country.filter((country) =>
		country.name.common.toLowerCase().includes(filter.toLowerCase())
	)
	const handlefilterChange = (event) => {
		setFilter(event.target.value)
	}
	return (
		<div>
			<h2>Countries.</h2>
			<Filter filter={filter} handlefilterChange={handlefilterChange} />
			<Countries filteredCountries={filteredCountries} />
		</div>
	)
}

export default App
